window.onload=function(){var cookieMessageValue=document.cookie.replace(/(?:(?:^|.*;\s*)cookiemessage\s*\=\s*([^;]*).*$)|^.*$/,"$1");if(cookieMessageValue==0){document.getElementById('cookie-message').style.display='block';}else if(cookieMessageValue==1){document.getElementById('cookie-message').style.display='none';}
document.getElementById('cookie-close').onclick=function(){document.getElementById('cookie-message').style.display='none';document.cookie="cookiemessage=1";}};